// src/pages/RegisterPage.js

import React, { useState } from 'react';
import axiosInstance from '../api/axiosInstance';
import { useNavigate, Link } from 'react-router-dom';

const RegisterPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        address: '',
        password: ''
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Client-side form validation function
    const validateForm = () => {
        // Name: Min 20, Max 60 characters
        if (formData.name.length < 20 || formData.name.length > 60) {
            alert('Name must be between 20 and 60 characters.');
            return false;
        }
        // Address: Max 400 characters
        if (formData.address.length > 400) {
            alert('Address cannot exceed 400 characters.');
            return false;
        }
        // Password: 8-16 characters, 1 uppercase, 1 special character
        const passwordRegex = /^(?=.*[A-Z])(?=.*[!@#$%^&*])(?=.{8,16})/;
        if (!passwordRegex.test(formData.password)) {
            alert('Password must be 8-16 characters, with at least one uppercase letter and one special character.');
            return false;
        }
        // Basic email validation
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(formData.email)) {
            alert('Please enter a valid email address.');
            return false;
        }
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) {
            return; // Stop if validation fails
        }
        try {
            const response = await axiosInstance.post('/register', formData);
            console.log(response.data);
            alert('Registration successful!');
            navigate('/login'); // Redirect to login page
        } catch (error) {
            console.error(error.response.data);
            // Display specific error message from backend if available
            alert(error.response.data.message || 'Registration failed. Please try again.');
        }
    };

    return (
        <div className="container">
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
                <button type="submit">Register</button>
            </form>
            <p>Already have an account? <Link to="/login" className="link">Login here</Link></p>
        </div>
    );
};

export default RegisterPage;
