// frontend/src/pages/DashboardPage.js

import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axiosInstance';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const DashboardPage = () => {
  const [role, setRole] = useState(null);
  const [adminData, setAdminData] = useState(null);
  const [storeOwnerData, setStoreOwnerData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedToken = jwt_decode(token);
      setRole(decodedToken.role);
      if (decodedToken.role === 'SystemAdmin') {
        fetchAdminDashboard(token);
      } else if (decodedToken.role === 'StoreOwner') {
        fetchStoreOwnerDashboard(token);
      }
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const fetchAdminDashboard = async (token) => {
    try {
      const response = await axiosInstance.get('/dashboard/admin', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAdminData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchStoreOwnerDashboard = async (token) => {
    try {
      const response = await axiosInstance.get('/dashboard/store-owner', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStoreOwnerData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // Render content based on role
  if (!role) {
    return <p>Loading...</p>;
  }

  if (role === 'SystemAdmin') {
    return (
      <div>
        <h2>Admin Dashboard</h2>
        {adminData ? (
          <div>
            <p>Total Users: {adminData.totalUsers}</p>
            <p>Total Stores: {adminData.totalStores}</p>
            <p>Total Ratings: {adminData.totalRatings}</p>
          </div>
        ) : (
          <p>Loading admin data...</p>
        )}
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  if (role === 'StoreOwner') {
    return (
      <div>
        <h2>Store Owner Dashboard</h2>
        {storeOwnerData ? (
          <div>
            <p>Store Name: {storeOwnerData.storeName}</p>
            <p>Average Rating: {storeOwnerData.avgRating ? parseFloat(storeOwnerData.avgRating).toFixed(2) : 'N/A'}</p>
            <h3>Ratings Submitted for Your Store:</h3>
            <ul>
              {storeOwnerData.ratings.map(r => (
                <li key={r.id}>Rating: {r.rating}</li>
              ))}
            </ul>
          </div>
        ) : (
          <p>Loading store data...</p>
        )}
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }

  return <p>Access denied. Please log in with a valid role.</p>;
};

export default DashboardPage;
