// frontend/src/components/RatingForm.js

import React, { useState } from 'react';
import axiosInstance from '../api/axiosInstance';

const RatingForm = ({ storeId, onRatingSubmitted }) => {
  const [rating, setRating] = useState(1);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You must be logged in to submit a rating.');
      return;
    }
    try {
      await axiosInstance.post('/ratings', { store_id: storeId, rating }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Rating submitted successfully!');
      onRatingSubmitted(); // Callback to refresh the stores list
    } catch (error) {
      console.error('Rating submission failed:', error);
      alert('Failed to submit rating.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Rate this store (1-5):
        <input type="number" min="1" max="5" value={rating} onChange={(e) => setRating(parseInt(e.target.value))} />
      </label>
      <button type="submit">Submit Rating</button>
    </form>
  );
};

export default RatingForm;
