// frontend/src/pages/DashboardPage.js

import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axiosInstance';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode'; // You need to install this package

const DashboardPage = () => {
  const [role, setRole] = useState(null);
  const [adminData, setAdminData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decodedToken = jwt_decode(token);
      setRole(decodedToken.role);
      if (decodedToken.role === 'SystemAdmin') {
        fetchAdminDashboard(token);
      }
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const fetchAdminDashboard = async (token) => {
    try {
      const response = await axiosInstance.get('/dashboard/admin', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAdminData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (role === 'SystemAdmin') {
    return (
      <div>
        <h2>Admin Dashboard</h2>
        {adminData ? (
          <div>
            <p>Total Users: {adminData.totalUsers}</p>
            <p>Total Stores: {adminData.totalStores}</p>
            <p>Total Ratings: {adminData.totalRatings}</p>
          </div>
        ) : (
          <p>Loading admin data...</p>
        )}
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  }
  return <p>Access denied.</p>; // Show this if not an admin
};

export default DashboardPage;
